.zoomer,
.zoomerOut {
    -webkit-animation-iteration-count: 1;
    animation-timing-function: cubic-bezier(.5, .2, .3, 1);
    animation-duration: 1s
}

@-webkit-keyframes zoomer {
    0% {
        -webkit-transform: scale(.3)
    }

    100% {
        -webkit-transform: scale(1)
    }
}

